import React, { useState } from 'react';
import './CategoryCarousel.css';

const categories = [
    { id: 1, name: 'Celulares', image: 'https://via.placeholder.com/100' },
    { id: 2, name: 'Ropa', image: 'https://via.placeholder.com/100' },
    { id: 3, name: 'Calzado', image: 'https://via.placeholder.com/100' },
    { id: 4, name: 'Electrodomésticos', image: 'https://via.placeholder.com/100' },
    { id: 5, name: 'Repuestos', image: 'https://via.placeholder.com/100' },
    { id: 6, name: 'Juguetes', image: 'https://via.placeholder.com/100' },
    { id: 7, name: 'Accesorios', image: 'https://via.placeholder.com/100' },
    { id: 8, name: 'Deportes', image: 'https://via.placeholder.com/100' },
];

const itemsPerPage = 7; // Número de categorías visibles por vez

const CategoryCarousel = () => {
    const [startIndex, setStartIndex] = useState(0);

    // Función para mover al producto anterior
    const handlePrev = () => {
        setStartIndex((prevIndex) =>
            prevIndex === 0 ? categories.length - itemsPerPage : prevIndex - 1
        );
    };

    // Función para mover al siguiente producto
    const handleNext = () => {
        setStartIndex((prevIndex) =>
            prevIndex + itemsPerPage >= categories.length ? 0 : prevIndex + 1
        );
    };

    // Calcular las categorías visibles
    const visibleCategories = categories.slice(
        startIndex,
        startIndex + itemsPerPage
    );

    return (
        <div className="category-carousel">
            {/* Botón anterior */}
            <button className="nav-button prev" onClick={handlePrev}>
                ◀
            </button>

            {/* Lista de categorías */}
            <div className="category-list">
                {visibleCategories.map((category) => (
                    <div key={category.id} className="category-item">
                        <img
                            src={category.image}
                            alt={category.name}
                            className="category-image"
                        />
                        <p className="category-name">{category.name}</p>
                    </div>
                ))}
            </div>

            {/* Botón siguiente */}
            <button className="nav-button next" onClick={handleNext}>
                ▶
            </button>

            {/* Puntos de paginación */}
            <div className="pagination-dots">
                {Array.from({
                    length: Math.ceil(categories.length / itemsPerPage),
                }).map((_, index) => (
                    <span
                        key={index}
                        className={`dot ${
                            Math.floor(startIndex / itemsPerPage) === index
                                ? 'active'
                                : ''
                        }`}
                    ></span>
                ))}
            </div>
        </div>
    );
};

export default CategoryCarousel;
