import React, { useState } from 'react';
import './ProductCarousel.css';

const ProductCarousel = ({ products }) => {
    const [startIndex, setStartIndex] = useState(0);
    const itemsPerPage = 5; // Número de productos visibles

    const handlePrev = () => {
        setStartIndex((prevIndex) =>
            prevIndex === 0 ? Math.max(0, products.length - itemsPerPage) : prevIndex - itemsPerPage
        );
    };

    const handleNext = () => {
        setStartIndex((prevIndex) =>
            prevIndex + itemsPerPage >= products.length ? 0 : prevIndex + itemsPerPage
        );
    };

    const visibleProducts = products.slice(startIndex, startIndex + itemsPerPage);

    return (
        <div className="product-carousel">
            <button className="nav-button prev" onClick={handlePrev}>
                ◀
            </button>
            <div className="product-list">
                {visibleProducts.map((product) => (
                    <div key={product.asin} className="product-card">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="product-image"
                        />
                        <div className="product-info">
                            <h3 className="product-name">{product.name}</h3>
                            <div className="product-prices">
                                <span className="product-price">${product.price}</span>
                                {product.originalPrice && (
                                    <span className="product-original-price">
                                        ${product.originalPrice}
                                    </span>
                                )}
                            </div>
                            {product.discount && (
                                <span className="product-discount">
                                    {product.discount}% OFF
                                </span>
                            )}
                            {product.freeShipping && (
                                <span className="product-shipping">Envío gratis</span>
                            )}
                        </div>
                    </div>
                ))}
            </div>
            <button className="nav-button next" onClick={handleNext}>
                ▶
            </button>
        </div>
    );
};

export default ProductCarousel;
