import React, { useState } from 'react';
import './ProductCarousel.css';

const ProductCarousel = ({ products }) => {
    const [startIndex, setStartIndex] = useState(0);
    const itemsPerPage = 4; // Número de productos visibles por vez

    // Función para mover al producto anterior
    const handlePrev = () => {
        setStartIndex((prevIndex) =>
            prevIndex === 0 ? Math.max(0, products.length - itemsPerPage) : prevIndex - 1
        );
    };

    // Función para mover al siguiente producto
    const handleNext = () => {
        setStartIndex((prevIndex) =>
            prevIndex + itemsPerPage >= products.length ? 0 : prevIndex + 1
        );
    };

    // Productos visibles en el carrusel
    const visibleProducts = products.slice(startIndex, startIndex + itemsPerPage);

    // Verifica si hay suficientes productos para los botones de navegación
    const showButtons = products.length > itemsPerPage;

    return (
        <div className="product-carousel">
            {/* Botón anterior */}
            {showButtons && (
                <button className="nav-button prev" onClick={handlePrev}>
                    ◀
                </button>
            )}

            {/* Lista de productos */}
            <div className="product-list">
                {visibleProducts.map((product) => (
                    <div key={product.asin || product.id} className="product-card">
                        <img
                            src={product.image}
                            alt={product.name}
                            className="product-image"
                        />
                        <h2 className="product-name">{product.name}</h2>
                        <p className="product-price">${product.price}</p>
                        {product.isOnSale && <span className="sale-badge">En Oferta</span>}
                    </div>
                ))}
            </div>

            {/* Botón siguiente */}
            {showButtons && (
                <button className="nav-button next" onClick={handleNext}>
                    ▶
                </button>
            )}

            {/* Paginación con puntos */}
            <div className="pagination-dots">
                {Array.from({ length: Math.ceil(products.length / itemsPerPage) }).map(
                    (_, index) => (
                        <span
                            key={index}
                            className={`dot ${
                                Math.floor(startIndex / itemsPerPage) === index
                                    ? 'active'
                                    : ''
                            }`}
                        ></span>
                    )
                )}
            </div>
        </div>
    );
};

export default ProductCarousel;
