import React from 'react';
import './Products.css';
import CategoryCarousel from '../../components/CategoryCarousel/CategoryCarousel';
import ProductCarousel from '../../components/ProductCarousel/ProductCarousel';

const Products = () => {
    // Datos ficticios para productos
    const allProducts = [
        { id: 1, name: 'Producto 1', price: 10, category: 'Ropa', image: 'https://via.placeholder.com/150', isOnSale: true },
        { id: 2, name: 'Producto 2', price: 20, category: 'Electrónica', image: 'https://via.placeholder.com/150', isOnSale: false },
        { id: 3, name: 'Producto 3', price: 30, category: 'Ropa', image: 'https://via.placeholder.com/150', isOnSale: true },
        { id: 4, name: 'Producto 4', price: 40, category: 'Hogar', image: 'https://via.placeholder.com/150', isOnSale: false },
        { id: 5, name: 'Producto 5', price: 50, category: 'Hogar', image: 'https://via.placeholder.com/150', isOnSale: true },
        { id: 6, name: 'Producto 6', price: 15, category: 'Electrónica', image: 'https://via.placeholder.com/150', isOnSale: false },
    ];

    // Categorías extraídas dinámicamente
    const categories = [...new Set(allProducts.map(product => product.category))];

    return (
        <div className="products-page">
            <h1 className="products-title">Catálogo de Productos</h1>

            {/* Carrusel de categorías */}
            <CategoryCarousel categories={categories} />

            {/* Carruseles de productos por categoría */}
            {categories.map((category) => (
                <div key={category} className="category-section">
                    <h2 className="category-title">{category}</h2>
                    <div className="carousel-container">
                        <button className="nav-button prev">◀</button>
                        <ProductCarousel
                            products={allProducts.filter(product => product.category === category)}
                        />
                        <button className="nav-button next">▶</button>
                    </div>
                    <button className="view-all-button">Ver todo {category}</button>
                </div>
            ))}
        </div>
    );
};

export default Products;
