import React, { useEffect, useState } from 'react';
import './Home.css';
import Banner from '../../components/Banner/Banner'; // Importar el componente Banner

const Home = () => {
    const [featuredProducts, setFeaturedProducts] = useState([]); // Estado para productos destacados
    const [loading, setLoading] = useState(true); // Estado de carga
    const [error, setError] = useState(null); // Estado de error

    // Obtener productos destacados desde el backend
    useEffect(() => {
        const fetchFeaturedProducts = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/products/featured'); // Ruta al backend
                if (!response.ok) {
                    throw new Error('Error al obtener productos destacados');
                }
                const data = await response.json();
                setFeaturedProducts(data); // Guardar los productos en el estado
            } catch (err) {
                setError(err.message); // Manejar errores
            } finally {
                setLoading(false); // Finalizar carga
            }
        };

        fetchFeaturedProducts();
    }, []);

    // Mostrar mientras se cargan los productos
    if (loading) return <p>Cargando productos destacados...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div className="home">
            <main className="home-content">
                {/* Banner */}
                <Banner />

                {/* Productos destacados */}
                <section className="home-featured">
                    <h2>Productos Destacados</h2>
                    <div className="featured-grid">
                        {featuredProducts.map((product) => (
                            <div key={product.asin} className="product-card">
                                <img
                                    src={product.image}
                                    alt={product.name}
                                    className="product-image"
                                />
                                <h3>{product.name}</h3>
                                <p>Precio: ${product.price}</p>
                                <p>{product.description}</p>
                            </div>
                        ))}
                    </div>
                </section>
            </main>
        </div>
    );
};

export default Home;
