import React, { useState } from 'react';
import './ProductCarousel.css';

const ProductCarousel = ({ products }) => {
    const [startIndex, setStartIndex] = useState(0);
    const itemsPerPage = 5; // Número de productos visibles por vez

    // Función para mover al producto anterior
    const handlePrev = () => {
        setStartIndex((prevIndex) =>
            prevIndex === 0 ? Math.max(0, products.length - itemsPerPage) : prevIndex - itemsPerPage
        );
    };

    // Función para mover al siguiente producto
    const handleNext = () => {
        setStartIndex((prevIndex) =>
            prevIndex + itemsPerPage >= products.length ? 0 : prevIndex + itemsPerPage
        );
    };

    // Productos visibles en el carrusel
    const visibleProducts = products.slice(startIndex, startIndex + itemsPerPage);

    // Imagen genérica
    const defaultImage = 'https://via.placeholder.com/150';

    // Mostrar botones solo si hay suficientes productos
    const showButtons = products.length > itemsPerPage;

    return (
        <div className="product-carousel">
            {/* Botón anterior */}
            {showButtons && (
                <button
                    className="nav-button prev"
                    onClick={handlePrev}
                    aria-label="Anterior"
                >
                    ◀
                </button>
            )}

            {/* Lista de productos */}
            <div className="product-list">
                {visibleProducts.map((product) => (
                    <div key={product.asin} className="product-card">
                        <img
                            src={product.image || defaultImage} // Si no hay imagen, usa la genérica
                            alt={product.name}
                            className="product-image"
                        />
                        <div className="product-info">
                            <h3 className="product-name">{product.name}</h3>
                            <p className="product-price">${product.price}</p>
                        </div>
                    </div>
                ))}
            </div>

            {/* Botón siguiente */}
            {showButtons && (
                <button
                    className="nav-button next"
                    onClick={handleNext}
                    aria-label="Siguiente"
                >
                    ▶
                </button>
            )}
        </div>
    );
};

export default ProductCarousel;
