import React, { useState } from 'react';
import './Products.css';

const Products = () => {
    // Datos ficticios para productos
    const allProducts = [
        { id: 1, name: 'Producto 1', price: 10, category: 'Ropa', image: 'https://via.placeholder.com/150' },
        { id: 2, name: 'Producto 2', price: 20, category: 'Electrónica', image: 'https://via.placeholder.com/150' },
        { id: 3, name: 'Producto 3', price: 30, category: 'Ropa', image: 'https://via.placeholder.com/150' },
        { id: 4, name: 'Producto 4', price: 40, category: 'Hogar', image: 'https://via.placeholder.com/150' },
    ];

    const [filteredProducts, setFilteredProducts] = useState(allProducts);

    // Filtrar productos por categoría
    const handleFilter = (category) => {
        if (category === 'Todos') {
            setFilteredProducts(allProducts);
        } else {
            setFilteredProducts(allProducts.filter(product => product.category === category));
        }
    };

    return (
        <div className="products-page">
            <h1 className="products-title">Catálogo de Productos</h1>
            
            {/* Filtros */}
            <div className="filters">
                <button onClick={() => handleFilter('Todos')}>Todos</button>
                <button onClick={() => handleFilter('Ropa')}>Ropa</button>
                <button onClick={() => handleFilter('Electrónica')}>Electrónica</button>
                <button onClick={() => handleFilter('Hogar')}>Hogar</button>
            </div>
            
            {/* Grilla de productos */}
            <div className="products-grid">
                {filteredProducts.map(product => (
                    <div key={product.id} className="product-card">
                        <img src={product.image} alt={product.name} className="product-image" />
                        <h2 className="product-name">{product.name}</h2>
                        <p className="product-price">${product.price}</p>
                        <button className="product-button">Añadir al carrito</button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Products;
